 ToDoTask(3, "Book doctor's appointment", Icons.Rounded.Home, Color(0xFF64B5F6), Color(0xFFE3F2FD)),
 ToDoTask(4, "Update CV", Icons.Rounded.Edit, Color(0xFF81C784), Color(0xFFE8F5E9)),
 ToDoTask(5, "Brainstorm ideas for team meeting", Icons.Rounded.FavoriteBorder, Color(0xFFBA68C8), Color(0xFFF3E5F5)),
 ToDoTask(6, "Call mom", Icons.Rounded.Call, Color(0xFF4DB6AC), Color(0xFFE0F2F1))
 )
 )
)

// The main screen Composable
@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun ToDoScreen() {
 // A state for the bottom navigation selection
 var selectedItem by remember { mutableIntStateOf(0) }
 val navItems = listOf("To-do", "Today", "Focus")

 Scaffold(
 containerColor = Color(0xFFF9F9F9), // A light greyish background color
 topBar = {
 TopAppBar(
 title = {
 Text("To-Do's", fontWeight = FontWeight.Bold, fontSize = 24.sp)
 },
 colors = TopAppBarDefaults.topAppBarColors(
 containerColor = Color.Transparent
 )
 )
 },
 bottomBar = {
 // The bottom bar contains both the input field and the navigation
 Column(modifier = Modifier.background(MaterialTheme.colorScheme.surface)) {
 ToDoInputBar()
 NavigationBar {
 navItems.forEachIndexed { index, item ->
 NavigationBarItem(
 icon = {
 Icon(
 imageVector = when (index) {
 0 -> Icons.Default.CheckCircle
 1 -> Icons.Default.DateRange
 else -> Icons.Default.CheckCircle // Placeholder
 },
 contentDescription = item
 )
 },
 label = { Text(item) },
 selected = selectedItem == index,
 onClick = { selectedItem = index }
 )
 }
 }
 }
 }
 ) { innerPadding ->
 ToDoList(
 sections = dummyTasks,
 modifier = Modifier.padding(innerPadding)
 )
 }
}

// Composable for the list of tasks
@Composable
fun ToDoList(sections: List<TaskSection>, modifier: Modifier = Modifier) {
 LazyColumn(
 modifier = modifier.padding(horizontal = 16.dp),
 contentPadding = PaddingValues(bottom = 16.dp)
 ) {
 sections.forEach { section ->
 item {
 TaskSectionHeader(
 title = section.title,
 showSwipeToAdd = section.title == "LOW"
 )
 }
 items(section.tasks) { task ->
 ToDoTaskItem(task = task, isHighlighted = section.isHighlighted)
 }
 item {
 Spacer(modifier = Modifier.height(16.dp))
 }
 }
 }
}

// Composable for a section header (e.g., "HIGH (1)")
@Composable
fun TaskSectionHeader(title: String, showSwipeToAdd: Boolean) {
 Row(
 modifier = Modifier
 .fillMaxWidth()
 .padding(vertical = 8.dp),
 verticalAlignment = Alignment.CenterVertically,
 horizontalArrangement = Arrangement.SpaceBetween
 ) {
 Text(
 text = title,
 style = MaterialTheme.typography.bodySmall,
 color = Color.Gray,
 fontWeight = FontWeight.Bold
 )
 if (showSwipeToAdd) {
 Row(verticalAlignment = Alignment.CenterVertically) {
 Text(
 text = "SWIPE TO ADD",
 style = MaterialTheme.typography.labelSmall,
 color = Color.LightGray
 )
 Spacer(modifier = Modifier.width(4.dp))
 Icon(
 imageVector = Icons.Default.Add,
 contentDescription = "Add",
 tint = Color.LightGray,
 modifier = Modifier.size(14.dp)
 )
 }
 }
 }
}

// Composable for a single task item in the list
@Composable
fun ToDoTaskItem(task: ToDoTask, isHighlighted: Boolean) {
 val backgroundColor = if (isHighlighted) Color(0xFFFFF0E1) else MaterialTheme.colorScheme.surface

 Surface(
 modifier = Modifier
 .fillMaxWidth()
 .padding(vertical = 4.dp),
 shape = RoundedCornerShape(12.dp),
 color = backgroundColor,
 border = if (isHighlighted) BorderStroke(1.dp, Color(0xFFE57373).copy(alpha = 0.5f)) else null
 ) {
 Row(
 modifier = Modifier.padding(16.dp),
 verticalAlignment = Alignment.CenterVertically
 ) {
 Box(
 modifier = Modifier
 .size(32.dp)
 .clip(CircleShape)
 .background(task.iconBackgroundColor),
 contentAlignment = Alignment.Center
 ) {
 Icon(
 imageVector = task.icon,
 contentDescription = null,
 tint = task.iconColor,
 modifier = Modifier.size(20.dp)
 )
 }

 Spacer(modifier = Modifier.width(16.dp))

 Text(
 text = task.text,
 style = MaterialTheme.typography.bodyLarge,
 modifier = Modifier.weight(1f),
 maxLines = 1,
 overflow = TextOverflow.Ellipsis
 )

 Spacer(modifier = Modifier.width(16.dp))

 Icon(
 imageVector = Icons.Outlined.DateRange,
 contentDescription = "Mark as complete",
 tint = Color.Gray
 )
 }
 }
}

// Composable for the input bar at the bottom
@Composable
fun ToDoInputBar() {
 Surface(
 modifier = Modifier.padding(16.dp),
 shape = RoundedCornerShape(12.dp),
 color = MaterialTheme.colorScheme.surface,
 shadowElevation = 4.dp
 ) {
 Row(
 modifier = Modifier
 .fillMaxWidth()
 .padding(horizontal = 16.dp, vertical = 8.dp),
 verticalAlignment = Alignment.CenterVertically
 ) {
 Text(
 text = "Type to begin",
 color = Color.Gray,
 modifier = Modifier.weight(1f)
 )
 Icon(
 imageVector = Icons.Default.ThumbUp,
 contentDescription = "Voice input",
 tint = Color.Gray
 )
 }
 }
}


// --- Preview ---
@Preview(showBackground = true, widthDp = 360, heightDp = 780)
@Composable
fun ToDoScreenPreview() {
 // You might want to wrap this in your app's theme
 MaterialTheme {
 ToDoScreen()
 }
}
