// A simple data class to represent a to-do task
data class ToDoTask(
 val id: Int,
 val text: String,
 val icon: ImageVector,
 val iconColor: Color,
 val iconBackgroundColor: Color
)

// A data class to represent a section of tasks
data class TaskSection(
 val title: String,
 val tasks: List<ToDoTask>,
 val isHighlighted: Boolean = false // To highlight "Review monthly budget"
)

// --- Dummy Data inspired by the image ---
val dummyTasks = listOf(
 TaskSection(
 title = "HIGH (1)",
 tasks = listOf(
 ToDoTask(1, "Pay electricity bill", Icons.Rounded.Edit, Color(0xFFD9A507), Color(0xFFFFFBE6))
 )
 ),
 TaskSection(
 title = "MEDIUM (1)",
 tasks = listOf(
 ToDoTask(2, "Review monthly budget", Icons.Rounded.Edit, Color(0xFFE57373), Color(0xFFFFF5F5))
 ),
 isHighlighted = true
 ),
 TaskSection(
